(200, 'diaper_reduce', 'diaper_reduce.png', 1, 'Swear off Disposables', 'You can embark on a psychologically questionable program of very early toilet training or switch to cloth diapers.', 0, 0, 260, 0, 0, 116480, NULL, 0, 0, 0, 0, '2007-11-12 13:49:16'),
(200, 'diaper_sorted', 'diaper_sorted.png', 2, 'Sort for Recycling', 'Actually, some disposable diapers can be composted, though perhaps not in your local community garden.', 0, 0, 240, 0, 0, 0, NULL, 0, 0, 0, 0, '2007-11-14 16:09:19'),
(200, 'diaper_reuse', 'diaper_reuse.png', 3, 'Use it Again!', 'Scrape it clean and put it right back. Why not?', 0, 0, 230, 0, 0, 0, NULL, 0, 0, 0, 0, '2007-11-12 13:49:16'),
