(9097, 'next', 'next.png', 1, '', '', 0, 0, 390, 0, 0, 0, NULL, 0, 0, 0, 0, '2007-11-12 20:18:08'),
(9099, 'next', 'next.png', 1, '', '', 0, 0, 390, 0, 0, 0, NULL, 0, 0, 0, 0, '2007-11-12 20:18:08'),
(9100, 'towel_unsorted', 'towel_unsorted.png', 2, 'Toss it in the Garbage', 'New Yorkers throw away 18,556 tons of towels, sheets and pillowcases and a total of about 190,000 tons of recyclable textiles a year.', 0, 0, 9145, 0, 18556, 0, NULL, 0, 0, 0, 0, '2007-11-14 15:38:44'),
(9100, 'towel_divert', 'towel_divert.png', 3, 'Drop it off at Goodwill', 'One person''s garbage is another one''s gold. You may be sick of that chartreuse terry cloth but it could be just the thing for another New Yorker. Eventually, getting rid of it will be that person''s responsibility.', 0, 0, 9140, 0, 9278, 9278, NULL, 0, 0, 0, 0, '2007-11-12 18:47:50'),
(9100, 'towel_reuse', 'towel_reuse.png', 1, 'Reuse it as a Rag', 'This will keep it out of the landfill a while longer and save you the expense of buying sponges, paper towels and Swiffers.', 0, 0, 9140, 0, 9278, 9278, NULL, 0, 0, 0, 0, '0000-00-00 00:00:00'),
(9140, 'next', 'next.png', 1, '', '', 0, 0, 390, 0, 0, 0, NULL, 0, 0, 0, 0, '2007-11-12 20:18:08'),
(9145, 'next', 'next.png', 1, '', '', 0, 0, 390, 0, 0, 0, NULL, 0, 0, 0, 0, '2007-11-12 20:18:08'),
(9150, 'compost_sorted', 'compost_sorted.png', 2, 'Sort it for Curbside Pickup', 'The Department of Sanitation currently collects limited amounts of yard waste, fall leaves and Christmas trees. If New Yorkers went all out and composted food scraps and other compostables with our yard waste, up to 30 percent of the city''s refuse by some counts could avoid the landfill.', 0, 0, 9192, 745000, 0, 0, NULL, 0, 0, 0, 0, '2007-11-14 15:44:30'),
(9150, 'compost_unsorted', 'compost_unsorted.png', 1, 'Toss it in the Can', 'Food waste accounts for about 22 percent of all residential garbage thrown away in NYC, with lawn clippings, dried leaves, branches and the like making up another 6 to 7.', 0, 0, 9190, 0, 745000, 0, NULL, 0, 0, 0, 0, '2007-11-14 15:38:44'),
(9150, 'compost_divert', 'compost_divert.png', 3, 'Put it in your Home Compost Bin', 'A number of organizations, including the Department of Sanitation, environmental groups and the city''s botanical gardens, offer instruction on how New Yorkers can compost safely in small spaces. Many community gardens also maintain compost bins.', 0, 0, 9191, 0, 0, 745000, NULL, 0, 0, 0, 0, '2007-11-14 15:38:44'),
(9190, 'next', 'next.png', 1, '', '', 0, 0, 390, 0, 0, 0, NULL, 0, 0, 0, 0, '2007-11-12 20:18:08'),
(9191, 'next', 'next.png', 1, '', '', 0, 0, 390, 0, 0, 0, NULL, 0, 0, 0, 0, '2007-11-12 20:18:08'),
