(9200, 'diaper_reuse', 'diaper_reuse.png', 3, 'Use it Again!', 'Scrape it clean and put it right back. Why not?', 0, 0, 9230, 0, 0, 0, NULL, 0, 0, 0, 0, '0000-00-00 00:00:00'),
(9200, 'diaper_reduce', 'diaper_reduce.png', 1, 'Swear off Disposables', 'You can embark on a psychologically questionable program of very early toilet training or switch to cloth diapers.', 0, 0, 9260, 0, 0, 116480, NULL, 0, 0, 0, 0, '0000-00-00 00:00:00'),
(9200, 'diaper_unsorted', 'diaper_unsorted.png', 5, 'Toss it in the Can', 'The average American infant goes through 1,800 to 2,000 diapers every year.', 0, 0, 9261, 0, 116480, 0, NULL, 0, 0, 0, 0, '2007-11-14 15:43:27'),
(9200, 'diaper_sorted', 'diaper_sorted.png', 2, 'Sort for Recycling', 'Actually, some disposable diapers can be composted, though perhaps not in your local community garden.', 0, 0, 9240, 0, 0, 0, NULL, 0, 0, 0, 0, '2007-11-14 15:43:27'),
